import React from "react";
import FriendCard from "./FriendCard";
import Friend from "../../../../models/Friend";
import { fetchFriendsBegin } from "../../../../redux/actions/actions";
import { connect } from "react-redux";
import AppState from "../../../../redux/store/store";
import SearchComponent from "./SearchComponent";

interface IProps {
    friendsList: Friend[];
    fetchFriends: Function;
}

class FriendsList extends React.Component<IProps, {}> {
    constructor(props: IProps) {
        super(props);
        this.state = {
            friendsList: []
        }
      }

      componentDidMount() {
        this.props.fetchFriends();
      }


    render() {
        return (
            <div>
                {
                    this.props.friendsList.map((friend, i) => {
                        return (
                            <div key={i}>
                                <FriendCard
                                    image="data:image/png;base64,"
                                    name={friend.profile_name}
                                    username={friend.username}
                                />
                            </div>
                        )
                    })
                }
                <SearchComponent/>
            </div>
        )
    }
}

const mapStateToProps = (state: AppState) => {
    return {
      friendsList: state.friends
    }
  }
  
  const mapDispatchToProps = (dispatch: any) => {
    return {
      fetchFriends: () => dispatch(fetchFriendsBegin())
    }
  }

export default connect(mapStateToProps, mapDispatchToProps)(FriendsList)